package test.com;

public class Test03switch2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("switch");
		//key:정수,문자형,문자열
		char key = 'B';
		switch (key) {
		case 'A':
			System.out.println((int)key);
			break;
		case 'B':
			System.out.println((int)key);
			break;

		default:
			break;
		}


		
		
	}

}
